mapper.select:
		SELECT

		FROM f_coupons_config t


  				AND t.coupons_config_id = #{couponsConfigId} 


  				AND t.coupon_id = #{couponId} 


  				AND t.coupon_key = #{couponKey} 


  				AND t.coupon_valtype = #{couponValtype} 


  				AND t.coupon_val1 = #{couponVal1} 


  				AND t.coupon_val2 = #{couponVal2} 


  				AND t.create_time = #{createTime} 


  				AND t.update_time = #{updateTime}
