mapper.select:
		SELECT

		FROM f_coupons t


  				AND t.id = #{id} 


  				AND t.coupon_name = #{couponName} 


  				AND t.coupon_desc = #{couponDesc} 


  				AND t.coupon_type = #{couponType} 


  				AND t.coupon_val = #{couponVal} 


  				AND t.start_time = #{startTime} 


  				AND t.expire_time = #{expireTime} 


  				AND t.coupon_stat = #{couponStat} 


  				AND t.create_time = #{createTime}
