mapper.select:
		SELECT

		FROM f_coupons_user_order_log t


  				AND t.id = #{id} 


  				AND t.coupon_user_id = #{couponUserId} 


  				AND t.sn = #{sn}


  				AND t.order_detail_id = #{orderDetailId} 


  				AND t.bef_order_amount = #{befOrderAmount} 


  				AND t.bef_service_money = #{befServiceMoney} 


  				AND t.bef_ele_money = #{befEleMoney} 


  				AND t.aft_service_money = #{aftServiceMoney} 


  				AND t.aft_ele_money = #{aftEleMoney} 


  				AND t.aft_order_amount = #{aftOrderAmount} 


  				AND t.create_time = #{createTime}
