mapper.select:
		SELECT

		FROM f_order_recharge t


  				AND t.oid = #{oid} 


  				AND t.uid = #{uid} 


  				AND t.sn = #{sn} 


  				AND t.otype = #{otype} 


  				AND t.platformtype = #{platformtype} 


  				AND t.amount = #{amount} 


  				AND t.remark = #{remark} 


  				AND t.partySN = #{partysn} 


  				AND t.createtime = #{createtime} 


  				AND t.discount = #{discount} 


  				AND t.status = #{status}
