mapper.select:
		SELECT

		FROM f_pile_charg_card t


  				AND t.pcid = #{pcid} 


  				AND t.SN = #{sn} 


  				AND t.password = #{password} 


  				AND t.pcstate = #{pcstate} 


  				AND t.expiretime = #{expiretime} 


  				AND t.createtime = #{createtime} 


  				AND t.activation = #{activation} 


  				AND t.uid = #{uid} 


  				AND t.banbale = #{banbale} 


  				AND t.usertype = #{usertype}
