mapper.select:
		SELECT

		FROM f_pile t


  				AND t.pid = #{pid} 


  				AND t.pname = #{pname} 


  				AND t.detail = #{detail} 


  				AND t.sid = #{sid} 


  				AND t.longitude = #{longitude} 


  				AND t.latitude = #{latitude} 


  				AND t.support = #{support} 


  				AND t.icon = #{icon} 


  				AND t.usecount = #{usecount} 


  				AND t.pstate = #{pstate} 


  				AND t.guncount = #{guncount} 


  				AND t.buildstate = #{buildstate} 


  				AND t.gainmode = #{gainmode} 


  				AND t.createtime = #{createtime} 


  				AND t.haslock = #{haslock} 


  				AND t.spacecount = #{spacecount} 


  				AND t.provinceid = #{provinceid} 


  				AND t.cityid = #{cityid} 


  				AND t.areaid = #{areaid} 


  				AND t.voltage = #{voltage} 


  				AND t.current = #{current} 


  				AND t.power = #{power} 


  				AND t.address = #{address} 


  				AND t.puid = #{puid} 


  				AND t.trialStatus = #{trialstatus} 


  				AND t.ptype = #{ptype} 


  				AND t.potype = #{potype} 


  				AND t.opentime = #{opentime} 


  				AND t.endtime = #{endtime} 


  				AND t.comunicatestate = #{comunicatestate} 


  				AND t.successcount = #{successcount} 


  				AND t.failcount = #{failcount} 


  				AND t.pilenum = #{pilenum} 


  				AND t.model = #{model} 


  				AND t.manufacture = #{manufacture} 


  				AND t.pileNumLength = #{pilenumlength} 


  				AND t.upt_time = #{uptTime}
