mapper.select:
		SELECT

		FROM f_refund_record t


  				AND t.id = #{id} 


  				AND t.uid = #{uid} 


  				AND t.refundAmount = #{refundamount} 


  				AND t.refundReasonId = #{refundreasonid} 


  				AND t.othReason = #{othreason} 


  				AND t.status = #{status} 


  				AND t.refundFailReason = #{refundfailreason} 


  				AND t.createtime = #{createtime} 


  				AND t.successtime = #{successtime} 


  				AND t.failtime = #{failtime}
