mapper.select:
SELECT

FROM f_user t


        AND t.uid = #{uid}


        AND t.uname = #{uname}


        AND t.phone = #{phone}


        AND t.pwd = #{pwd}


        AND t.salt = #{salt}


        AND t.paypwd = #{paypwd}


        AND t.paysalt = #{paysalt}


        AND t.nickname = #{nickname}


        AND t.nickicon = #{nickicon}


        AND t.lid = #{lid}


        AND t.lastIP = #{lastip}


        AND t.nowIP = #{nowip}


        AND t.lasttime = #{lasttime}


        AND t.nowtime = #{nowtime}


        AND t.regIP = #{regip}


        AND t.regtime = #{regtime}


        AND t.cid = #{cid}


        AND t.score = #{score}


        AND t.iscompany = #{iscompany}


        AND t.sex = #{sex}


        AND t.birsday = #{birsday}


        AND t.provinceid = #{provinceid}


        AND t.cityid = #{cityid}


        AND t.countyid = #{countyid}


        AND t.address = #{address}


        AND t.baseinformation = #{baseinformation}


        AND t.realnameauth = #{realnameauth}


        AND t.identitycard = #{identitycard}


        AND t.countNum = #{countnum}


        AND t.userStatus = #{userstatus}


        AND t.typeicon = #{typeicon}
